import React, { useState, useEffect } from "react";

const apiBase = "http://localhost:3001/api";

function App() {
  const [qr, setQr] = useState(null);
  const [status, setStatus] = useState("loading");
  const [number, setNumber] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [webhook, setWebhook] = useState("");

  // AJUSTADO: Busca o QR code e status no endpoint /api/qr
  const fetchStatus = async () => {
    const resp = await fetch(`${apiBase}/qr`);
    const data = await resp.json();
    setStatus(data.status);
    setQr(data.qr || null);
  };

  const fetchMessages = async () => {
    const resp = await fetch(`${apiBase}/messages`);
    const data = await resp.json();
    setMessages(data);
  };

  useEffect(() => {
    fetchStatus();
    fetchMessages();
    const interval = setInterval(() => {
      fetchStatus();
      fetchMessages();
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleSendMessage = async () => {
    await fetch(`${apiBase}/message`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ number, message })
    });
    setMessage("");
    fetchMessages();
  };

  const handleReconnect = async () => {
    await fetch(`${apiBase}/reconnect`, { method: "POST" });
    fetchStatus();
  };

  const handleSetWebhook = async () => {
    await fetch(`${apiBase}/webhook`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ url: webhook })
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-blue-900 flex flex-col items-center justify-center p-4">
      <div className="bg-white shadow-xl rounded-xl p-8 w-full max-w-lg">
        <h1 className="text-2xl font-bold mb-4 text-blue-700">WhatsApp API Dashboard</h1>
        <div className="mb-4 flex items-center justify-between">
          <span>Status: <span className={`font-bold ${status === "authenticated" ? "text-green-500" : "text-red-500"}`}>{status}</span></span>
          <button onClick={handleReconnect} className="px-2 py-1 bg-blue-600 text-white rounded hover:bg-blue-700">Reconectar</button>
        </div>
        {qr && (
          <div className="mb-4 flex flex-col items-center">
            <img src={qr} alt="QR Code" className="w-48 h-48 border" />
            <span className="mt-2 text-gray-600 text-sm">Escaneie para conectar</span>
          </div>
        )}
        <div className="mb-4">
          <input
            type="text"
            placeholder="Número (ex: 5511999999999)"
            className="w-full px-2 py-1 border rounded mb-2"
            value={number}
            onChange={e => setNumber(e.target.value)}
          />
          <textarea
            placeholder="Mensagem"
            className="w-full px-2 py-1 border rounded mb-2"
            value={message}
            onChange={e => setMessage(e.target.value)}
          />
          <button onClick={handleSendMessage} className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 w-full">Enviar Mensagem</button>
        </div>
        <div className="mb-4">
          <input
            type="text"
            placeholder="Webhook URL"
            className="w-full px-2 py-1 border rounded"
            value={webhook}
            onChange={e => setWebhook(e.target.value)}
          />
          <button onClick={handleSetWebhook} className="mt-2 px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700 w-full">Registrar Webhook</button>
        </div>
        <div>
          <h2 className="font-bold text-gray-700 mb-2">Mensagens Recentes</h2>
          {messages.length === 0 ? (
            <div className="text-gray-400 italic">Nenhuma mensagem recebida.</div>
          ) : (
            <ul className="max-h-40 overflow-auto">
              {messages.map((m, i) => (
                <li key={i} className="mb-2 border-b pb-1">
                  <span className="font-mono text-xs text-gray-500">{m.from}</span><br/>
                  <span>{m.body}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
      <footer className="text-sm text-gray-400 mt-8">
        Desenvolvido por você com <span className="text-red-500">❤</span>
      </footer>
    </div>
  );
}

export default App;
