import React, { useState, useEffect } from "react";
import { FiSend, FiRefreshCw, FiCheckCircle, FiXCircle, FiLink, FiUsers } from "react-icons/fi";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import dayjs from "dayjs";

const apiBase = "http://localhost:3001/api";

function App() {
  const [qr, setQr] = useState(null);
  const [status, setStatus] = useState("loading");
  const [number, setNumber] = useState("");
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [sentMessages, setSentMessages] = useState([]);
  const [webhook, setWebhook] = useState("");
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch status and QR
  const fetchStatus = async () => {
    setLoading(true);
    const resp = await fetch(`${apiBase}/qr`);
    const data = await resp.json();
    setStatus(data.status);
    setQr(data.qr || null);
    setLoading(false);
  };

  // Fetch messages
  const fetchMessages = async () => {
    const resp = await fetch(`${apiBase}/messages`);
    const data = await resp.json();
    setMessages(data);
    // Simula contatos recentes dos remetentes das mensagens
    setContacts([...new Set(data.map(m => m.from))]);
  };

  useEffect(() => {
    fetchStatus();
    fetchMessages();
    const interval = setInterval(() => {
      fetchStatus();
      fetchMessages();
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  // Enviar mensagem
  const handleSendMessage = async () => {
    if (!number || !message) {
      toast.error("Preencha número e mensagem!");
      return;
    }
    try {
      const resp = await fetch(`${apiBase}/message`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ number, message })
      });
      const res = await resp.json();
      if (res.status) {
        toast.success("Mensagem enviada!");
        setSentMessages([
          ...sentMessages,
          { to: number, body: message, timestamp: Date.now() }
        ]);
        setMessage("");
      } else {
        toast.error(res.error || "Erro ao enviar.");
      }
    } catch (err) {
      toast.error("Falha ao conectar à API.");
    }
  };

  // Reconnect
  const handleReconnect = async () => {
    await fetch(`${apiBase}/reconnect`, { method: "POST" });
    fetchStatus();
    toast.info("Reconectando...");
  };

  // Registrar Webhook
  const handleSetWebhook = async () => {
    if (!webhook) {
      toast.error("Informe a URL do webhook.");
      return;
    }
    await fetch(`${apiBase}/webhook`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ url: webhook })
    });
    toast.success("Webhook registrado.");
  };

  // Envio de mídia (imagem)
  const handleSendMedia = async (e) => {
    const file = e.target.files[0];
    if (!file || !number) return;
    const reader = new FileReader();
    reader.onload = async () => {
      try {
        await fetch(`${apiBase}/media`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            number,
            caption: message,
            mediaUrl: reader.result // Precisa de endpoint que aceite base64!
          })
        });
        toast.success("Mídia enviada!");
      } catch {
        toast.error("Erro ao enviar mídia.");
      }
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-gray-800 flex flex-col">
      <ToastContainer position="top-right" autoClose={3500} />
      {/* Header */}
      <header className="bg-gray-950 text-white flex items-center px-8 py-4 shadow-lg">
        <FiUsers className="text-3xl mr-2 text-blue-500" />
        <h1 className="text-2xl font-bold tracking-wide">WhatsApp API Dashboard</h1>
        <span className="ml-auto flex items-center">
          {status === "authenticated" ? (
            <FiCheckCircle className="mr-2 text-green-500" />
          ) : (
            <FiXCircle className="mr-2 text-red-500 animate-pulse" />
          )}
          <span className={`font-semibold ${status === "authenticated" ? "text-green-400" : "text-red-400"}`}>
            {status === "authenticated" ? "Conectado" : status === "scan" ? "Escaneie o QR" : "Carregando..."}
          </span>
          <button onClick={handleReconnect} className="ml-6 px-3 py-1 bg-blue-700 hover:bg-blue-800 rounded text-white font-bold flex items-center">
            <FiRefreshCw className="mr-1" /> Reconectar
          </button>
        </span>
      </header>

      {/* Main Dashboard */}
      <div className="flex-grow flex flex-row justify-center items-start gap-8 py-8">
        {/* QR Code Card */}
        <div className="bg-gray-900 rounded-xl shadow-lg p-6 w-96 flex flex-col items-center">
          <h2 className="text-lg font-bold mb-2 text-white">Autenticação</h2>
          {qr && status === "scan" ? (
            <>
              <img src={qr} alt="QR Code" className="w-56 h-56 border mb-3" />
              <span className="text-gray-300 text-sm">Escaneie para conectar</span>
            </>
          ) : (
            <div className="w-56 h-56 flex items-center justify-center text-gray-500">
              {loading ? (
                <div className="animate-spin rounded-full h-10 w-10 border-t-2 border-b-2 border-blue-500"></div>
              ) : (
                <FiCheckCircle className="text-5xl text-green-600" />
              )}
            </div>
          )}
        </div>

        {/* Envio de mensagem / mídia */}
        <div className="bg-gray-900 rounded-xl shadow-lg p-6 w-[28rem] flex flex-col">
          <h2 className="text-lg font-bold mb-2 text-white">Enviar Mensagem</h2>
          <input
            type="text"
            placeholder="Número (ex: 5511999999999)"
            className="w-full px-2 py-1 rounded mb-2 border border-gray-700 bg-gray-800 text-white"
            value={number}
            onChange={e => setNumber(e.target.value)}
          />
          <textarea
            placeholder="Mensagem"
            className="w-full px-2 py-1 rounded mb-2 border border-gray-700 bg-gray-800 text-white"
            value={message}
            onChange={e => setMessage(e.target.value)}
          />
          <button onClick={handleSendMessage} className="px-4 py-2 bg-green-600 hover:bg-green-700 rounded text-white font-bold flex items-center justify-center mb-2">
            <FiSend className="mr-1" /> Enviar Mensagem
          </button>
          <label className="flex items-center text-white font-semibold cursor-pointer">
            <input type="file" accept="image/*" className="hidden" onChange={handleSendMedia} />
            <span className="bg-purple-700 hover:bg-purple-800 px-3 py-1 rounded flex items-center">
              <FiLink className="mr-1" /> Enviar Imagem
            </span>
          </label>
          <div className="mt-4">
            <input
              type="text"
              placeholder="Webhook URL"
              className="w-full px-2 py-1 rounded border border-gray-700 bg-gray-800 text-white"
              value={webhook}
              onChange={e => setWebhook(e.target.value)}
            />
            <button onClick={handleSetWebhook} className="mt-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded text-white font-bold w-full flex items-center justify-center">
              <FiLink className="mr-1" /> Registrar Webhook
            </button>
          </div>
        </div>

        {/* Mensagens Recentes */}
        <div className="bg-gray-900 rounded-xl shadow-lg p-6 w-[32rem] flex flex-col">
          <h2 className="text-lg font-bold mb-2 text-white">Mensagens Recentes</h2>
          <div className="flex flex-row gap-2 mb-2">
            <input
              type="text"
              placeholder="Buscar mensagem..."
              className="flex-1 px-2 py-1 rounded border border-gray-700 bg-gray-800 text-white"
              onChange={e => {
                const search = e.target.value.toLowerCase();
                fetchMessages();
                setMessages(messages.filter(
                  m => m.body.toLowerCase().includes(search) || m.from.includes(search)
                ));
              }}
            />
            <span className="text-gray-400">Total: {messages.length}</span>
          </div>
          <div className="max-h-56 overflow-auto">
            {messages.length === 0 ? (
              <div className="text-gray-400 italic">Nenhuma mensagem recebida.</div>
            ) : (
              <ul>
                {messages.map((m, i) => (
                  <li key={i} className="mb-2 border-b border-gray-700 pb-2">
                    <span className="font-mono text-xs text-blue-400">{m.from}</span>
                    <span className="text-gray-400 ml-2">{dayjs(m.timestamp).format("DD/MM HH:mm")}</span>
                    <br />
                    <span className="text-white">{m.body}</span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>

      {/* Sidebar de contatos recentes */}
      <aside className="fixed right-0 top-0 h-screen bg-gray-800 text-white shadow-xl px-6 pt-8 w-64">
        <h2 className="text-lg font-bold mb-4">Contatos Recentes</h2>
        <ul>
          {contacts.length === 0 ? (
            <li className="text-gray-400">Nenhum contato.</li>
          ) : (
            contacts.map((c, i) => (
              <li key={i} className="mb-2">{c}</li>
            ))
          )}
        </ul>
        <div className="mt-8 text-xs text-gray-400">Uptime: {(performance.now()/1000/60).toFixed(1)} min</div>
      </aside>
      {/* Footer */}
      <footer className="bg-gray-950 text-gray-500 text-sm py-6 px-8 mt-8">
        Desenvolvido por você com <span className="text-red-500">❤</span> — {dayjs().format("YYYY")}
      </footer>
    </div>
  );
}

export default App;
